package hw01;

public class hw3 {
	public void draw3(int n) {
		for (int j = 1; j <= n; j++) {

			for (int i = 1; i <= n; i++) {
				System.out.print(i);
			}
			System.out.println();

		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		hw3 hw3 = new hw3();
		hw3.draw3(4);

	}

}
