package hw01;

public class hw5 {
	public void draw5(int n) {
		for(int i=1;i<=n;i++) {
			for(int j=1;j<=n;j++) {
				System.out.print(i);
			}
			System.out.println();
		}
		
		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		hw5 hw5 = new hw5();
		hw5.draw5(4);
		

	}

}
