package hw01;

public class hw6 {
	public void draw6(int n) {

		for(int i=n;i>0;i--) {
			for(int j=n;j>0;j--) {
				System.out.print(i);
			}
			System.out.println();
		}		

		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		hw6 hw6 = new hw6();
		hw6.draw6(3);
		

	}

}
